-------------------------------------- Define table------------------------------------/*Column order tweaked a bit with Column Tetris search fromhttps://www.2ndquadrant.com/en/blog/on-rocks-and-sand/Not neeed in this table, but I want to keep the structure parallel(ish) toneed_history, where keeping row sizes doesn *does* matter. -- DPA*/BEGIN;DROP TABLE IF EXISTS ascendco.need CASCADE;CREATE TABLE IF NOT EXISTS ascendco.need (	id uuid NOT NULL DEFAULT NULL,	hsys_id uuid NOT NULL DEFAULT NULL,	facility_id uuid NOT NULL DEFAULT NULL,	item_id uuid NOT NULL DEFAULT NULL,	percent_down double precision NOT NULL DEFAULT NULL,	created_dts timestamptz NOT NULL DEFAULT NOW(),	next_case_dts timestamp NOT NULL DEFAULT TIMESTAMP 'epoch',	flow integer NOT NULL DEFAULT NULL,	qty_circulation integer NOT NULL DEFAULT NULL,	qty_ready_everywhere integer NOT NULL DEFAULT NULL,	qty_ready_here integer NOT NULL DEFAULT NULL,	qty_sched_everywhere integer NOT NULL DEFAULT NULL,	qty_sched_here integer NOT NULL DEFAULT NULL,	qty_sterile_everywhere integer NOT NULL DEFAULT NULL,	qty_sterile_here integer NOT NULL DEFAULT NULL,	qty_suggest_sterilize integer NOT NULL DEFAULT NULL,	qty_total integer NOT NULL DEFAULT NULL,	ready_vs_need_defecit integer NOT NULL DEFAULT NULL,	sched_sort integer NOT NULL DEFAULT NULL,	sort_rank integer NOT NULL DEFAULT NULL,	suggest_transport boolean NOT NULL DEFAULT NULL,	next_needed citext NOT NULL DEFAULT NULL,	or_name citext NOT NULL DEFAULT NULL,	status citext NOT NULL DEFAULT NULL,	status_sort citext NOT NULL DEFAULT NULL,	when_ citext NOT NULL DEFAULT NULL,    qty_here integer NOT NULL DEFAULT 0);ALTER TABLE ascendco.need	ADD CONSTRAINT need_id_pkey    PRIMARY KEY (id);ALTER TABLE ascendco.need	OWNER TO user_change_structure;COMMIT;-------------------------------------- Build indexes------------------------------------CREATE INDEX need_facility_id_ix_fkey	ON ascendco.need (facility_id); -- Not sure if this will be useful, it depends how big the table is.-- need_history dropped for now. We can revisit it, once Welsh has a think about what to include.