-------------------------------------- Define table------------------------------------BEGIN;DROP TABLE IF EXISTS ascendco.specialty;CREATE TABLE ascendco.specialty (	id uuid NOT NULL,	marked_for_deletion bool NOT NULL DEFAULT false,	data_file_id uuid NOT NULL,	name_ extensions.citext COLLATE pg_catalog.default NOT NULL,	name_short extensions.citext COLLATE pg_catalog.default NOT NULL);ALTER TABLE ascendco.specialty	ADD CONSTRAINT specialty_id_pkey	PRIMARY KEY (id);ALTER TABLE ascendco.specialty	OWNER TO user_change_structure;COMMIT;-------------------------------------- Build indexes-------------------------------------------------------------------------- Add triggers------------------------------------CREATE TRIGGER trigger_specialty_after_delete	AFTER DELETE	ON ascendco.specialty	REFERENCING OLD TABLE AS deleted_rows	FOR EACH STATEMENT	EXECUTE PROCEDURE ascendco.trigger_function_log_deletion_count();CREATE TRIGGER trigger_specialty_before_truncate	BEFORE TRUNCATE	ON ascendco.specialty	FOR EACH STATEMENT	EXECUTE PROCEDURE ascendco.trigger_function_log_truncation_count();