-------------------------------------- Define table------------------------------------BEGIN;DROP TABLE IF EXISTS ascendco.sterilize_method;CREATE TABLE ascendco.sterilize_method  (	id uuid NOT NULL,	marked_for_deletion bool NOT NULL DEFAULT false,	data_file_id uuid NOT NULL,	name_ extensions.citext COLLATE pg_catalog.default NOT NULL);ALTER TABLE ascendco.sterilize_method	ADD CONSTRAINT sterilize_method_id_pkey	PRIMARY KEY (id);ALTER TABLE ascendco.sterilize_method	OWNER TO user_change_structure;COMMIT;-------------------------------------- Build indexes-------------------------------------------------------------------------- Add triggers------------------------------------CREATE TRIGGER trigger_sterilize_method_after_delete	AFTER DELETE	ON ascendco.sterilize_method	REFERENCING OLD TABLE AS deleted_rows	FOR EACH STATEMENT	EXECUTE PROCEDURE ascendco.trigger_function_log_deletion_count();CREATE TRIGGER trigger_sterilize_method_before_truncate	BEFORE TRUNCATE	ON ascendco.sterilize_method	FOR EACH STATEMENT	EXECUTE PROCEDURE ascendco.trigger_function_log_truncation_count();