-------------------------------------- Define table------------------------------------BEGIN;DROP TABLE IF EXISTS ascendco.sterilize_params;CREATE TABLE ascendco.sterilize_params  (	id uuid NOT NULL,	marked_for_deletion bool NOT NULL DEFAULT false,	data_file_id uuid NOT NULL,	hsys_id uuid NOT NULL,	sterilize_method_id uuid NOT NULL,	name_ extensions.citext COLLATE pg_catalog.default NOT NULL);ALTER TABLE ascendco.sterilize_params 	ADD CONSTRAINT sterilize_params_id_pkey 	PRIMARY KEY (id);ALTER TABLE ascendco.sterilize_params 	OWNER TO user_change_structure;COMMIT;-------------------------------------- Build indexes-------------------------------------- Nope. Postgres won't even bother with an index for searches on such a tiny table.-- Note: You always need an index for a unique constraint. PG defines that automatically for the PRIMARY.-------------------------------------- Add triggers------------------------------------CREATE TRIGGER trigger_sterilize_params_after_delete	AFTER DELETE	ON ascendco.sterilize_params	REFERENCING OLD TABLE AS deleted_rows 	FOR EACH STATEMENT	EXECUTE PROCEDURE ascendco.trigger_function_log_deletion_count();CREATE TRIGGER trigger_sterilize_params_before_truncate	BEFORE TRUNCATE	ON ascendco.sterilize_params	FOR EACH STATEMENT	EXECUTE PROCEDURE ascendco.trigger_function_log_truncation_count();