-------------------------------------- Define table------------------------------------BEGIN;DROP TABLE IF EXISTS ascendco.sterilizer;CREATE TABLE ascendco.sterilizer  (	id uuid NOT NULL,	marked_for_deletion bool NOT NULL DEFAULT false,	data_file_id uuid NOT NULL,	facility_id uuid NOT NULL,	sterilize_method_id uuid NOT NULL,	name_ extensions.citext COLLATE pg_catalog.default NOT NULL);ALTER TABLE ascendco.sterilizer 	ADD CONSTRAINT sterilizer_id_pkey 	PRIMARY KEY (id);ALTER TABLE ascendco.sterilizer 	OWNER TO user_change_structure;COMMIT;-------------------------------------- Build indexes-------------------------------------- Nope. Postgres won't even bother with an index for searches on such a tiny table.-- Note: You always need an index for a unique constraint. PG defines that automatically for the PRIMARY.-------------------------------------- Add triggers------------------------------------CREATE TRIGGER trigger_sterilizer_after_delete	AFTER DELETE	ON ascendco.sterilizer	REFERENCING OLD TABLE AS deleted_rows 	FOR EACH STATEMENT	EXECUTE PROCEDURE ascendco.trigger_function_log_deletion_count();CREATE TRIGGER trigger_sterilizer_before_truncate	BEFORE TRUNCATE	ON ascendco.sterilizer	FOR EACH STATEMENT	EXECUTE PROCEDURE ascendco.trigger_function_log_truncation_count();