-------------------------------------- Define table------------------------------------BEGIN;DROP TABLE IF EXISTS ascendco.sterilizer_log;CREATE TABLE ascendco.sterilizer_log  (	id uuid NOT NULL,	log_type extensions.citext COLLATE pg_catalog.default NOT NULL,	summary_json jsonb NOT NULL DEFAULT '{}'::jsonb,	log_xml xml NOT NULL,	data_file_id uuid DEFAULT '00000000-0000-0000-0000-000000000000'::uuid);ALTER TABLE ascendco.sterilizer_log 	ADD CONSTRAINT sterilizer_log_id_pkey 	PRIMARY KEY (id);ALTER TABLE ascendco.sterilizer_log 	OWNER TO user_change_structure;COMMIT;-------------------------------------- Build indexes-------------------------------------- None yet.-------------------------------------- Add triggers------------------------------------CREATE TRIGGER trigger_sterilizer_log_after_delete	AFTER DELETE	ON ascendco.sterilizer_log	REFERENCING OLD TABLE AS deleted_rows 	FOR EACH STATEMENT	EXECUTE PROCEDURE ascendco.trigger_function_log_deletion_count();CREATE TRIGGER trigger_sterilizer_log_before_truncate	BEFORE TRUNCATE	ON ascendco.sterilizer_log	FOR EACH STATEMENT	EXECUTE PROCEDURE ascendco.trigger_function_log_truncation_count();