-------------------------------------- Define term table------------------------------------DROP TABLE IF EXISTS ascendco.term_analysis;CREATE TABLE IF NOT EXISTS ascendco.term_analysis (	id             uuid    NOT NULL DEFAULT NULL, -- [Item]ID	facility_id    uuid    NOT NULL DEFAULT NULL,	hsys_id        uuid    NOT NULL DEFAULT NULL,	term           citext  NOT NULL DEFAULT '',	cleaned_term   citext  NOT NULL DEFAULT '');ALTER TABLE ascendco.term_analysis	ADD CONSTRAINT term_analysis_pkey    PRIMARY KEY (id,facility_id);ALTER TABLE ascendco.term_analysis	OWNER TO user_change_structure;-------------------------------------- Build indexes-------------------------------------- Magic GiST index for K-NN search.CREATE INDEX term_analysis_term_ix_tgrm_gist	ON ascendco.term_analysis	USING gist(term COLLATE pg_catalog.default extensions.gist_trgm_ops);-------------------------------------- Add triggers-------------------------------------- None needed at the moment, but this might change to auto-populate and dirty custom_function.-------------------------------------- Add GRANTS-------------------------------------- Not normal, jamming these in for my quick tests.GRANT SELECT, INSERT, UPDATE, DELETE ON term_analysis TO postgres;GRANT SELECT, INSERT, UPDATE, DELETE ON term_analysis TO user_reporting;GRANT SELECT, INSERT, UPDATE, DELETE ON term_analysis TO user_iceberg;GRANT SELECT, INSERT, UPDATE, DELETE ON term_analysis TO user_change_structure;GRANT SELECT, INSERT, UPDATE, DELETE ON term_analysis TO user_sonar;