-------------------------------------- Define table------------------------------------BEGIN;DROP TABLE IF EXISTS dba.patch_log;CREATE TABLE IF NOT EXISTS dba.patch_log (	id             uuid         NOT NULL DEFAULT extensions.gen_random_uuid(),	patch_dts      timestamptz  NOT NULL DEFAULT now(), -- The timestamp of the transaction you're in.    server_address citext       NOT NULL DEFAULT inet_server_addr()::citext,    database_name  citext       NOT NULL DEFAULT current_database()::citext,    pg_version     citext       NOT NULL DEFAULT version()::citext,    user_name      user_name    NOT NULL DEFAULT current_user::user_name, -- user_name is a custom domain.    patch_name     citext_not_empty      NOT NULL DEFAULT NULL,    description    citext_not_empty      NOT NULL DEFAULT NULL,    patch_hash     text_not_empty        NOT NULL DEFAULT NULL);ALTER TABLE dba.patch_log	ADD CONSTRAINT patch_log_id_pkey    PRIMARY KEY (id);ALTER TABLE dba.patch_log	OWNER TO user_change_structure;COMMIT;-------------------------------------- Build indexes-------------------------------------- None needed at the moment.-------------------------------------- Add triggers-------------------------------------- None needed at the moment, but this might change to auto-populate and dirty custom_function.