-------------------------------------- Define table------------------------------------BEGIN;DROP TABLE IF EXISTS dba.test_case CASCADE;CREATE TABLE IF NOT EXISTS dba.test_case (    id                       uuid NOT NULL DEFAULT extensions.gen_random_uuid(),    added_dts                timestamptz                    NOT NULL DEFAULT now(), -- The timestamp of the transaction you're in.    object_class             citext_not_empty               NOT NULL DEFAULT NULL,    object_schema            citext_not_empty               NOT NULL DEFAULT NULL,    object_name              citext_not_empty               NOT NULL DEFAULT NULL,    function_signature       citext                         NOT NULL DEFAULT '',    test_name                citext_not_empty               NOT NULL DEFAULT NULL,    test_code                text_not_empty                 NOT NULL DEFAULT NULL,    setup_code               citext                         NOT NULL DEFAULT '',    teardown_code            citext                         NOT NULL DEFAULT '',    expected_response        citext                         NOT NULL DEFAULT '',    expected_error           citext                         NOT NULL DEFAULT '',    expected_outcome         test_outcome                   NOT NULL DEFAULT NULL,   -- Custom domain allowing 'pass', 'warning', 'fail', case-blind.    response_type            citext_not_empty               NOT NULL DEFAULT '',     -- text, numeric, record, JSON, like that.    comparison_options       jsonb                          NOT NULL DEFAULT '{"case":"blind"}'::jsonb,    run_as                   user_name[]                    NOT NULL DEFAULT NULL,   -- array of representative users to test as, checked against the user_name DOMAIN.    modifies_data            bool                           NOT NULL DEFAULT false,    rds_only                 bool                           NOT NULL DEFAULT false);ALTER TABLE dba.test_case    ADD CONSTRAINT test_case_id_pkey    PRIMARY KEY (id);ALTER TABLE dba.test_case	ADD CONSTRAINT test_case_unique_signature	UNIQUE (object_class,object_schema,object_name,function_signature,test_name);ALTER TABLE dba.test_case    OWNER TO user_change_structure;COMMIT;-------------------------------------- Build indexes-------------------------------------- None needed at the moment.-------------------------------------- Add triggers-------------------------------------- None needed at the moment, but this might change to auto-populate and dirty custom_function.