-------------------------------------- Define table------------------------------------BEGIN;DROP TABLE IF EXISTS dba.view_catalog;CREATE TABLE IF NOT EXISTS dba.view_catalog (	schema_name    citext  NOT NULL DEFAULT NULL,    view_name      citext  NOT NULL DEFAULT NULL,    description    citext  NOT NULL DEFAULT NULL,    domo_dataset   citext  NOT NULL DEFAULT NULL);ALTER TABLE dba.view_catalog	ADD CONSTRAINT view_catalog_id_pkey    PRIMARY KEY (schema_name,view_name);COMMENT ON TABLE dba.view_catalog IS	'Searchable descriptions of useful views. It''s all text, no validation of names, OIDs, etc.';ALTER TABLE dba.view_catalog	OWNER TO user_change_structure;COMMIT;-------------------------------------- Build indexes-------------------------------------- None needed at the moment.-------------------------------------- Add triggers-------------------------------------- None needed at the moment, but this might change to auto-populate and dirty custom_function.-------------------------------------- Add GRANTS-------------------------------------- Not normal, jamming these in for my quick tests.GRANT SELECT, INSERT, UPDATE, DELETE ON view_catalog TO rds_super;GRANT SELECT, INSERT, UPDATE, DELETE ON view_catalog TO user_reporting;GRANT SELECT, INSERT, UPDATE, DELETE ON view_catalog TO user_iceberg;GRANT SELECT, INSERT, UPDATE, DELETE ON view_catalog TO user_change_structure;GRANT SELECT, INSERT, UPDATE, DELETE ON view_catalog TO user_sonar;-------------------------------------------------- Register view------------------------------------------------