-- Create a function to accept an array of rows formatted as assembly_prods_v1 for UPSERT into assembly_prods.DROP FUNCTION IF EXISTS types_plus.insert_assembly_prods_v1 (types_plus.assembly_prods_v1[]);CREATE OR REPLACE FUNCTION types_plus.insert_assembly_prods_v1 (data_in types_plus.assembly_prods_v1[])  RETURNS intAS $BODY$-- The CTE below is a roundabout way of returning an insertion count from a pure SQL function in Postgres.with inserted_rows as (        INSERT INTO assembly_prods (			id,			data_file_id,			assembly_id,			item_prod_id,			created_dts,			updated_dts,			target,			found_,			repair,			backup,			actual,			assembly_pos,			marked_for_deletion,			created_by,			updated_by,			prod_alert_text_ack,			audit_events)        SELECT			rows_in.id,			rows_in.data_file_id,			rows_in.assembly_id,			rows_in.item_prod_id,			rows_in.created_dts,			rows_in.updated_dts,			rows_in.target,			rows_in.found_,			rows_in.repair,			rows_in.backup,			rows_in.actual,			rows_in.assembly_pos,			rows_in.marked_for_deletion,			rows_in.created_by,			rows_in.updated_by,			rows_in.prod_alert_text_ack,			rows_in.audit_events        FROM unnest(data_in) as rows_in        ON CONFLICT ON CONSTRAINT assembly_prods_pkey DO UPDATE SET			data_file_id = EXCLUDED.data_file_id,			assembly_id = EXCLUDED.assembly_id,			item_prod_id = EXCLUDED.item_prod_id,			created_dts = EXCLUDED.created_dts,			updated_dts = EXCLUDED.updated_dts,			target = EXCLUDED.target,			found_ = EXCLUDED.found_,			repair = EXCLUDED.repair,			backup = EXCLUDED.backup,			actual = EXCLUDED.actual,			assembly_pos = EXCLUDED.assembly_pos,			marked_for_deletion = EXCLUDED.marked_for_deletion,			created_by = EXCLUDED.created_by,			updated_by = EXCLUDED.updated_by,			prod_alert_text_ack = EXCLUDED.prod_alert_text_ack,			audit_events = EXCLUDED.audit_events        returning 1 as row_counter)    select sum(row_counter)::integer from inserted_rows;$BODY$LANGUAGE sql;ALTER FUNCTION types_plus.insert_assembly_prods_v1(types_plus.assembly_prods_v1[])	OWNER TO user_bender;