-- Create a view onto assembly_prods formatted as assembly_prods_v1.-- If assembly_prods changes, you can update the view to handle the modification smoothly.-- Note: This view is automatically a custom type, with an array type to match. See types_plus.insert_assembly_prods_v1.DROP VIEW IF EXISTS types_plus.assembly_prods_v1 CASCADE;-- Careful!CREATE OR REPLACE VIEW types_plus.assembly_prods_v1 AS select id,        data_file_id,        assembly_id,        item_prod_id,        created_dts,        updated_dts,        target,        found_,        repair,        backup,        actual,        assembly_pos,        marked_for_deletion,        created_by,        updated_by,        prod_alert_text_ack,        audit_events   from assembly_prods;ALTER TABLE types_plus.assembly_prods_v1    OWNER TO user_change_structure;