DROP TABLE IF EXISTS ascendco.tz_test;CREATE TABLE IF NOT EXISTS ascendco.tz_test (    tz_with_time_zone      timestamptz   NOT NULL DEFAULT 'epoch',    tz_without_time_zone   timestamp     NOT NULL DEFAULT 'epoch');ALTER TABLE ascendco.tz_test	OWNER TO user_change_structure;INSERT INTO tz_test VALUES (now(),now());SELECT       tz_with_time_zone                              AS with_raw,       timezone ('UTC',        tz_with_time_zone)     AS with_at_utc,       timezone ('US/Central', tz_with_time_zone)     AS with_at_central,       tz_without_time_zone                           AS without_raw,       timezone ('UTC',        tz_without_time_zone)  AS without_at_utc,       timezone ('US/Central', tz_without_time_zone)  AS without_at_central  from tz_testDROP TABLE IF EXISTS ascendco.tz_test;CREATE TABLE IF NOT EXISTS ascendco.tz_test (    tz_with_time_zone      timestamptz   NOT NULL DEFAULT 'epoch',    tz_without_time_zone   timestamp     NOT NULL DEFAULT 'epoch');ALTER TABLE ascendco.tz_test	OWNER TO user_change_structure;INSERT INTO tz_test VALUES (now(),now());SELECT 'with_raw'           as value_source, tz_with_time_zone                              UNION ALLSELECT 'with_at_utc'        as value_source, timezone ('UTC',        tz_with_time_zone)     UNION ALLSELECT 'with_at_central'    as value_source, timezone('US/Central',  tz_with_time_zone)     UNION ALLSELECT 'without_raw'        as value_source, tz_with_time_zone                              UNION ALLSELECT 'without_at_utc'     as value_source, timezone ('UTC',        tz_without_time_zone)  UNION ALLSELECT 'without_at_central' as value_source, timezone('US/Central',  tz_without_time_zone)FROM   tz_test