select qualified_name,       user_name,       privilege,       case when setting = 't' then 'True' else 'False' end  as setting  -- Native Boolean output is t or f, but different clients apply different formatting. So, to keep comparisons simple, apply a set formatting here.                from table_grants where table_name in 	('analytic_productivity',	'analytic_scan', 	'analytic_sterilizer_load', 	'analytic_sterilizer_loadinv', 	'assembly', 	'assembly_prods', 	'data_file_info', 	'domo_dataset', 	'facility', 	'facility_location', 	'hsys', 	'inv', 	'item', 	'item_type', 	'push_log', 	'record_changes_log', 	'record_changes_log_detail', 	'row_compare', 	'specialty', 	'sterilize_method', 	'sterilize_params', 	'sterilizer', 	'sterilizer_load', 	'sterilizer_log', 	'web_user')	and user_name in	('user_admin',	'user_backup',	'user_bender',	'user_change_structure',	'user_cleanup',	'user_domo_pull',	'user_iceberg',	'user_iceberg_remote',	'user_leviathan',	'user_reporting',	'user_saws',	'user_sonar')order by 	qualified_name,	user_name,	privilege;