DROP TABLE IF EXISTS api.sample_series;-- Define the table.CREATE TABLE IF NOT EXISTS api.sample_series (	id INTEGER NOT NULL DEFAULT 0 PRIMARY KEY,	stamp timestamptz NOT NULL DEFAULT NULL	);-- Generate the series and insert the results into the table.INSERT INTO api.sample_series (id,stamp)      SELECT hour_number as id,           '2019-01-01 00:00'::timestamp + interval '1 HOUR' * hour_number as stamp       FROM generate_series(1,8760,1) as hour_number;