-- Create a view onto activity formatted as activity_v2.-- If activity changes, you can update the view to handle the modification smoothly.-- Note: This view is automatically a custom type, with an array type to match. See types_plus.insert_activity_v2.DROP VIEW IF EXISTS types_plus.activity_v2 CASCADE;-- Careful!CREATE OR REPLACE VIEW types_plus.activity_v2 AS select id,        facility_id,        facility_activities_id,        web_user_id,        last_updated_by_data_file_id,        updated_dts,        created_dts,        done_dts,        start_dts,        duration_seconds,        marked_for_deletion,        other_desc,        created_by,        updated_by   from activity;ALTER TABLE types_plus.activity_v2    OWNER TO user_change_structure;