-- Create a view onto analytic_productivity formatted as analytic_productivity_v1.-- If analytic_productivity changes, you can update the view to handle the modification smoothly.-- Note: This view is automatically a custom type, with an array type to match. See types_plus.insert_analytic_productivity_v1.DROP VIEW IF EXISTS types_plus.analytic_productivity_v1 CASCADE;-- Careful!CREATE OR REPLACE VIEW types_plus.analytic_productivity_v1 AS select id,        data_file_id,        marked_for_deletion,        hsys_id,        facility_id,        facility_location_id,        specialty_id,        item_type_id,        user_name,        inv_name,        item_name,        tray_or_pack,        num_inst,        assembly_minutes,        pause_minutes,        points,        points_per_hour,        assembly_hour,        pause_hour,        start_utc,        start_local,        end_utc,        end_local   from analytic_productivity;ALTER TABLE types_plus.analytic_productivity_v1    OWNER TO user_change_structure;