-- Create a view onto analytic_scan formatted as analytic_scan_v1.-- If analytic_scan changes, you can update the view to handle the modification smoothly.-- Note: This view is automatically a custom type, with an array type to match. See types_plus.insert_analytic_scan_v1.DROP VIEW IF EXISTS types_plus.analytic_scan_v1 CASCADE;-- Careful!CREATE OR REPLACE VIEW types_plus.analytic_scan_v1 AS select id,        data_file_id,        marked_for_deletion,        hsys_id,        facility_id,        facility_location_id,        specialty_id,        scanned_type,        associated_to,        user_name,        inv_name,        num_inst,        tray_or_pack,        item_name,        scan_time_utc_dts,        scan_time_local_dts,        location_description   from analytic_scan;ALTER TABLE types_plus.analytic_scan_v1    OWNER TO user_change_structure;