-- Create a view onto analytic_sterilizer_load formatted as analytic_sterilizer_load_v1.-- If analytic_sterilizer_load changes, you can update the view to handle the modification smoothly.-- Note: This view is automatically a custom type, with an array type to match. See types_plus.insert_analytic_sterilizer_load_v1.DROP VIEW IF EXISTS types_plus.analytic_sterilizer_load_v1 CASCADE;-- Careful!CREATE OR REPLACE VIEW types_plus.analytic_sterilizer_load_v1 AS select id,        marked_for_deletion,        data_file_id,        sterilize_method_id,        sterilize_params_id,        sterilizer_id,        facility_id,        web_user_id,        status_building_dts,        status_building_local_dts,        status_done_dts,        status_done_local_dts,        status,        is_iuss,        instruments_count,        packs_count,        trays_count   from analytic_sterilizer_load;ALTER TABLE types_plus.analytic_sterilizer_load_v1    OWNER TO user_change_structure;