-- Create a view onto assembly formatted as assembly_v1.-- If assembly changes, you can update the view to handle the modification smoothly.-- Note: This view is automatically a custom type, with an array type to match. See types_plus.insert_assembly_v1.DROP VIEW IF EXISTS types_plus.assembly_v1 CASCADE;-- Careful!CREATE OR REPLACE VIEW types_plus.assembly_v1 AS select id,        data_file_id,        marked_for_deletion,        created_by_user_id,        done_by_user_id,        facility_location_id,        inv_id,        last_updated_by_data_file_id,        updated_by_user_id,        is_complete,        is_quick_turn,        con_id,        created_by,        difficulty,        quantity,        sequence_no,        num_inst,        points,        done_dts,        created_dts,        updated_dts,        missing_list,        priority,        status,        updated_by   from assembly;ALTER TABLE types_plus.assembly_v1    OWNER TO user_change_structure;