-- Create a function to accept an array of rows formatted as clinic_v1 for UPSERT into clinic.DROP FUNCTION IF EXISTS types_plus.insert_clinic_v1 (types_plus.clinic_v1[]);  CREATE OR REPLACE FUNCTION types_plus.insert_clinic_v1 (data_in types_plus.clinic_v1[])  RETURNS intAS $BODY$ -- The CTE below is a roundabout way of returning an insertion count from a pure SQL function in Postgres.with inserted_rows as (        INSERT INTO clinic (			id,			hsys_id,			created_dts,			updated_dts,			marked_for_deletion,			receive_at_clinic,			name_,			created_by,			updated_by)                  SELECT			rows_in.id,			rows_in.hsys_id,			rows_in.created_dts,			rows_in.updated_dts,			rows_in.marked_for_deletion,			rows_in.receive_at_clinic,			rows_in.name_,			rows_in.created_by,			rows_in.updated_by                      FROM unnest(data_in) as rows_in                  ON CONFLICT(id) DO UPDATE SET			hsys_id = EXCLUDED.hsys_id,			created_dts = EXCLUDED.created_dts,			updated_dts = EXCLUDED.updated_dts,			marked_for_deletion = EXCLUDED.marked_for_deletion,			receive_at_clinic = EXCLUDED.receive_at_clinic,			name_ = EXCLUDED.name_,			created_by = EXCLUDED.created_by,			updated_by = EXCLUDED.updated_by          returning 1 as row_counter)         select sum(row_counter)::integer from inserted_rows; $BODY$LANGUAGE sql;  ALTER FUNCTION types_plus.insert_clinic_v1(types_plus.clinic_v1[])	OWNER TO user_bender;