-- Create a casting function to convert domo_dataset rows into the compound type format domo_dataset_v1.-- If domo_dataset changes, you can update the casting to handle the modification smoothly.CREATE OR REPLACE FUNCTION types_plus.domo_dataset_to_v1 (domo_dataset_in domo_dataset)  RETURNS domo_dataset_v1AS $BODY$                   SELECT			domo_dataset_in.id,			domo_dataset_in.dataset_name,			domo_dataset_in.rows_count,			domo_dataset_in.columns_count,			domo_dataset_in.created_at_dts,			domo_dataset_in.updated_at_dts               $BODY$LANGUAGE sql;  ALTER FUNCTION types_plus.domo_dataset_to_v1 (domo_dataset_in domo_dataset)	OWNER TO user_bender;-- Create/recreate the CAST. Use this to convert domo_dataset rows into the domo_dataset_v1 compound type format using the following syntax:-- select domo_dataset::domo_dataset_v1 from domo_datasetDROP CAST IF EXISTS (domo_dataset as domo_dataset_v1);CREATE CAST (domo_dataset as domo_dataset_v1) WITH FUNCTION types_plus.domo_dataset_to_v1(domo_dataset);