-- Create a casting function to convert domo_passthrough rows into the compound type format domo_passthrough_v2.-- If domo_passthrough changes, you can update the casting to handle the modification smoothly.CREATE OR REPLACE FUNCTION types_plus.domo_passthrough_to_v2 (domo_passthrough_in domo_passthrough)  RETURNS domo_passthrough_v2AS $BODY$                   SELECT			domo_passthrough_in.key_supplement,			domo_passthrough_in.source_id,			domo_passthrough_in.hsys_id,			domo_passthrough_in.facility_id,			domo_passthrough_in.view_version,			domo_passthrough_in.dataset_name,			domo_passthrough_in.data,			domo_passthrough_in.from_dts,			domo_passthrough_in.to_dts               $BODY$LANGUAGE sql;  ALTER FUNCTION types_plus.domo_passthrough_to_v2 (domo_passthrough_in domo_passthrough)	OWNER TO user_bender;-- Create/recreate the CAST. Use this to convert domo_passthrough rows into the domo_passthrough_v2 compound type format using the following syntax:-- select domo_passthrough::domo_passthrough_v2 from domo_passthroughDROP CAST IF EXISTS (domo_passthrough as domo_passthrough_v2);CREATE CAST (domo_passthrough as domo_passthrough_v2) WITH FUNCTION types_plus.domo_passthrough_to_v2(domo_passthrough);