-- Create a view onto edge formatted as edge_v1.-- If edge changes, you can update the view to handle the modification smoothly.-- Note: This view is automatically a custom type, with an array type to match. See types_plus.insert_edge_v1.DROP VIEW IF EXISTS types_plus.edge_v1 CASCADE;-- Careful!CREATE OR REPLACE VIEW types_plus.edge_v1 AS select id,        sequence_,        from_scan_at_facility_id,        from_associated_to_id,        to_associated_to_id,        from_web_user_id,        from_assembly_id,        from_scan_id,        from_sterilizer_load_id,        from_facility_location_id,        from_facility_id,        from_washer_load_id,        from_scase_id,        from_clinic_id,        from_facility_department_id,        hsys_id,        to_scan_id,        to_scan_at_facility_id,        to_web_user_id,        to_user_name,        to_facility_location_id,        to_sterilizer_load_id,        to_washer_load_id,        to_facility_id,        to_scase_id,        to_clinic_id,        to_facility_department_id,        to_assembly_id,        from_node_dts,        to_node_dts,        created_dts,        updated_dts,        seconds,        num_inst,        inv_id,        from_node,        to_node,        from_to_node,        from_associated_to,        to_associated_to,        from_user_name,        source_,        is_fake,        path_   from edge;ALTER TABLE types_plus.edge_v1    OWNER TO user_change_structure;