-- Create a casting function to convert facility_activities rows into the compound type format facility_activities_v1.-- If facility_activities changes, you can update the casting to handle the modification smoothly.CREATE OR REPLACE FUNCTION types_plus.facility_activities_to_v1 (facility_activities_in facility_activities)  RETURNS facility_activities_v1AS $BODY$                   SELECT			facility_activities_in.id,			facility_activities_in.facility_id,			facility_activities_in.last_updated_by_data_file_id,			facility_activities_in.created_dts,			facility_activities_in.updated_dts,			facility_activities_in.marked_for_deletion,			facility_activities_in.name_,			facility_activities_in.created_by,			facility_activities_in.updated_by               $BODY$LANGUAGE sql;  ALTER FUNCTION types_plus.facility_activities_to_v1 (facility_activities_in facility_activities)	OWNER TO user_bender;-- Create/recreate the CAST. Use this to convert facility_activities rows into the facility_activities_v1 compound type format using the following syntax:-- select facility_activities::facility_activities_v1 from facility_activitiesDROP CAST IF EXISTS (facility_activities as facility_activities_v1);CREATE CAST (facility_activities as facility_activities_v1) WITH FUNCTION types_plus.facility_activities_to_v1(facility_activities);