-- Create a casting function to convert facility_department rows into the compound type format facility_department_v1.-- If facility_department changes, you can update the casting to handle the modification smoothly.CREATE OR REPLACE FUNCTION types_plus.facility_department_to_v1 (facility_department_in facility_department)  RETURNS facility_department_v1AS $BODY$                   SELECT			facility_department_in.id,			facility_department_in.facility_id,			facility_department_in.their_id,			facility_department_in.created_dts,			facility_department_in.updated_dts,			facility_department_in.marked_for_deletion,			facility_department_in.is_available_in_sonar,			facility_department_in.receive_at_dept,			facility_department_in.is_from_emr,			facility_department_in.name_,			facility_department_in.created_by,			facility_department_in.updated_by,			facility_department_in.label_name,			facility_department_in.inv_audit               $BODY$LANGUAGE sql;  ALTER FUNCTION types_plus.facility_department_to_v1 (facility_department_in facility_department)	OWNER TO user_bender;-- Create/recreate the CAST. Use this to convert facility_department rows into the facility_department_v1 compound type format using the following syntax:-- select facility_department::facility_department_v1 from facility_departmentDROP CAST IF EXISTS (facility_department as facility_department_v1);CREATE CAST (facility_department as facility_department_v1) WITH FUNCTION types_plus.facility_department_to_v1(facility_department);