-- Create a view onto facility_department formatted as facility_department_v1.-- If facility_department changes, you can update the view to handle the modification smoothly.-- Note: This view is automatically a custom type, with an array type to match. See types_plus.insert_facility_department_v1.DROP VIEW IF EXISTS types_plus.facility_department_v1 CASCADE;-- Careful!CREATE OR REPLACE VIEW types_plus.facility_department_v1 AS select id,        facility_id,        their_id,        created_dts,        updated_dts,        marked_for_deletion,        is_available_in_sonar,        receive_at_dept,        is_from_emr,        name_,        created_by,        updated_by,        label_name,        inv_audit   from facility_department;ALTER TABLE types_plus.facility_department_v1    OWNER TO user_change_structure;-- Create a casting function to convert facility_department rows into the compound type format facility_department_v1.-- If facility_department changes, you can update the casting to handle the modification smoothly.CREATE OR REPLACE FUNCTION types_plus.facility_department_to_v1 (facility_department_in facility_department)  RETURNS facility_department_v1AS $BODY$                   SELECT			facility_department_in.id,			facility_department_in.facility_id,			facility_department_in.their_id,			facility_department_in.created_dts,			facility_department_in.updated_dts,			facility_department_in.marked_for_deletion,			facility_department_in.is_available_in_sonar,			facility_department_in.receive_at_dept,			facility_department_in.is_from_emr,			facility_department_in.name_,			facility_department_in.created_by,			facility_department_in.updated_by,			facility_department_in.label_name,			facility_department_in.inv_audit               $BODY$LANGUAGE sql;  ALTER FUNCTION types_plus.facility_department_to_v1 (facility_department_in facility_department)	OWNER TO user_bender;-- Create/recreate the CAST. Use this to convert facility_department rows into the facility_department_v1 compound type format using the following syntax:-- select facility_department::facility_department_v1 from facility_departmentDROP CAST IF EXISTS (facility_department as facility_department_v1);CREATE CAST (facility_department as facility_department_v1) WITH FUNCTION types_plus.facility_department_to_v1(facility_department);-- Create a function to accept an array of rows formatted as facility_department_v1 for UPSERT into facility_department.DROP FUNCTION IF EXISTS types_plus.insert_facility_department_v1 (types_plus.facility_department_v1[]);  CREATE OR REPLACE FUNCTION types_plus.insert_facility_department_v1 (data_in types_plus.facility_department_v1[])  RETURNS intAS $BODY$ -- The CTE below is a roundabout way of returning an insertion count from a pure SQL function in Postgres.with inserted_rows as (        INSERT INTO facility_department (			id,			facility_id,			their_id,			created_dts,			updated_dts,			marked_for_deletion,			is_available_in_sonar,			receive_at_dept,			is_from_emr,			name_,			created_by,			updated_by,			label_name,			inv_audit)                  SELECT			rows_in.id,			rows_in.facility_id,			rows_in.their_id,			rows_in.created_dts,			rows_in.updated_dts,			rows_in.marked_for_deletion,			rows_in.is_available_in_sonar,			rows_in.receive_at_dept,			rows_in.is_from_emr,			rows_in.name_,			rows_in.created_by,			rows_in.updated_by,			rows_in.label_name,			rows_in.inv_audit                      FROM unnest(data_in) as rows_in                  ON CONFLICT(id) DO UPDATE SET			facility_id = EXCLUDED.facility_id,			their_id = EXCLUDED.their_id,			created_dts = EXCLUDED.created_dts,			updated_dts = EXCLUDED.updated_dts,			marked_for_deletion = EXCLUDED.marked_for_deletion,			is_available_in_sonar = EXCLUDED.is_available_in_sonar,			receive_at_dept = EXCLUDED.receive_at_dept,			is_from_emr = EXCLUDED.is_from_emr,			name_ = EXCLUDED.name_,			created_by = EXCLUDED.created_by,			updated_by = EXCLUDED.updated_by,			label_name = EXCLUDED.label_name,			inv_audit = EXCLUDED.inv_audit          returning 1 as row_counter)         select sum(row_counter)::integer from inserted_rows; $BODY$LANGUAGE sql;  ALTER FUNCTION types_plus.insert_facility_department_v1(types_plus.facility_department_v1[])	OWNER TO user_bender;