-- Create a view onto facility_department formatted as facility_department_v1.-- If facility_department changes, you can update the view to handle the modification smoothly.-- Note: This view is automatically a custom type, with an array type to match. See types_plus.insert_facility_department_v1.DROP VIEW IF EXISTS types_plus.facility_department_v1 CASCADE;-- Careful!CREATE OR REPLACE VIEW types_plus.facility_department_v1 AS select id,        facility_id,        their_id,        created_dts,        updated_dts,        marked_for_deletion,        is_available_in_sonar,        receive_at_dept,        is_from_emr,        name_,        created_by,        updated_by,        label_name,        inv_audit   from facility_department;ALTER TABLE types_plus.facility_department_v1    OWNER TO user_change_structure;