-- Create a casting function to convert facility_location rows into the compound type format facility_location_v1.-- If facility_location changes, you can update the casting to handle the modification smoothly.CREATE OR REPLACE FUNCTION types_plus.facility_location_to_v1 (facility_location_in facility_location)  RETURNS facility_location_v1AS $BODY$        SELECT			facility_location_in.id,			facility_location_in.marked_for_deletion,			facility_location_in.facility_id,			facility_location_in.type_,			facility_location_in.name_$BODY$LANGUAGE sql;ALTER FUNCTION types_plus.facility_location_to_v1 (facility_location_in facility_location) OWNER TO user_bender;-- Create/recreate the CAST. Use this to convert facility_location rows into the facility_location_v1 compound type format using the following syntax:-- select facility_location::facility_location_v1 from facility_locationDROP CAST IF EXISTS (facility_location as facility_location_v1);CREATE CAST (facility_location as facility_location_v1) WITH FUNCTION types_plus.facility_location_to_v1(facility_location);