-- Create a function to accept an array of rows formatted as facility_location_v1 for UPSERT into facility_location.DROP FUNCTION IF EXISTS types_plus.insert_facility_location_v1 (types_plus.facility_location_v1[]);CREATE OR REPLACE FUNCTION types_plus.insert_facility_location_v1 (data_in types_plus.facility_location_v1[])  RETURNS intAS $BODY$-- The CTE below is a roundabout way of returning an insertion count from a pure SQL function in Postgres.with inserted_rows as (        INSERT INTO facility_location (			id,			marked_for_deletion,			facility_id,			type_,			name_)        SELECT			rows_in.id,			rows_in.marked_for_deletion,			rows_in.facility_id,			rows_in.type_,			rows_in.name_        FROM unnest(data_in) as rows_in        ON CONFLICT(id) DO UPDATE SET			marked_for_deletion = EXCLUDED.marked_for_deletion,			facility_id = EXCLUDED.facility_id,			type_ = EXCLUDED.type_,			name_ = EXCLUDED.name_        returning 1 as row_counter)    select sum(row_counter)::integer from inserted_rows;$BODY$LANGUAGE sql;ALTER FUNCTION types_plus.insert_facility_location_v1(types_plus.facility_location_v1[]) OWNER TO user_bender;