-- Create a casting function to convert facility rows into the compound type format facility_v2.-- If facility changes, you can update the casting to handle the modification smoothly.CREATE OR REPLACE FUNCTION types_plus.facility_to_v2 (facility_in facility)  RETURNS facility_v2AS $BODY$                   SELECT			facility_in.id,			facility_in.hsys_id,			facility_in.production_label_form_id,			facility_in.permanent_label_form_id,			facility_in.missing_inst_label_form_id,			facility_in.facility_location_label_form_id,			facility_in.sterilizer_label_form_id,			facility_in.user_badge_label_form_id,			facility_in.workstation_label_form_id,			facility_in.default_spd_facility_id,			facility_in.created_dts,			facility_in.updated_dts,			facility_in.sonar_auto_logout_minutes,			facility_in.sonar_auto_logout_dialog_minutes,			facility_in.num_ors,			facility_in.sonar_go_live_date,			facility_in.marked_for_deletion,			facility_in.is_count_sheet_source_truth,			facility_in.hide_doc_names_for_stats,			facility_in.is_item_master_live,			facility_in.is_sonar_live,			facility_in.sterilizer_print_at_sterilizing,			facility_in.sterilizer_print_at_cooling,			facility_in.sterilizer_print_at_released,			facility_in.can_set_all_found_in_assembly,			facility_in.name_,			facility_in.their_id,			facility_in.created_by,			facility_in.updated_by,			facility_in.reporting_name,			facility_in.label_name,			facility_in.tz_name,			facility_in.sonar_name_full,			facility_in.sonar_count_sheet_form_name,			facility_in.sterilize_alert_level,			facility_in.sonar_scanner_stop_char,			facility_in.emr_location_name,			facility_in.sonar_features,			facility_in.interfaces,			facility_in.needs_scheduling,			facility_in.analytics_prefs               $BODY$LANGUAGE sql;  ALTER FUNCTION types_plus.facility_to_v2 (facility_in facility)	OWNER TO user_bender;-- Create/recreate the CAST. Use this to convert facility rows into the facility_v2 compound type format using the following syntax:-- select facility::facility_v2 from facilityDROP CAST IF EXISTS (facility as facility_v2);CREATE CAST (facility as facility_v2) WITH FUNCTION types_plus.facility_to_v2(facility);