-- Create a view onto facility formatted as facility_v2.-- If facility changes, you can update the view to handle the modification smoothly.-- Note: This view is automatically a custom type, with an array type to match. See types_plus.insert_facility_v2.DROP VIEW IF EXISTS types_plus.facility_v2 CASCADE;-- Careful!CREATE OR REPLACE VIEW types_plus.facility_v2 AS select id,        hsys_id,        production_label_form_id,        permanent_label_form_id,        missing_inst_label_form_id,        facility_location_label_form_id,        sterilizer_label_form_id,        user_badge_label_form_id,        workstation_label_form_id,        default_spd_facility_id,        created_dts,        updated_dts,        sonar_auto_logout_minutes,        sonar_auto_logout_dialog_minutes,        num_ors,        sonar_go_live_date,        marked_for_deletion,        is_count_sheet_source_truth,        hide_doc_names_for_stats,        is_item_master_live,        is_sonar_live,        sterilizer_print_at_sterilizing,        sterilizer_print_at_cooling,        sterilizer_print_at_released,        can_set_all_found_in_assembly,        name_,        their_id,        created_by,        updated_by,        reporting_name,        label_name,        tz_name,        sonar_name_full,        sonar_count_sheet_form_name,        sterilize_alert_level,        sonar_scanner_stop_char,        emr_location_name,        sonar_features,        interfaces,        needs_scheduling,        analytics_prefs   from facility;ALTER TABLE types_plus.facility_v2    OWNER TO user_change_structure;-- Create a casting function to convert facility rows into the compound type format facility_v2.-- If facility changes, you can update the casting to handle the modification smoothly.CREATE OR REPLACE FUNCTION types_plus.facility_to_v2 (facility_in facility)  RETURNS facility_v2AS $BODY$                   SELECT			facility_in.id,			facility_in.hsys_id,			facility_in.production_label_form_id,			facility_in.permanent_label_form_id,			facility_in.missing_inst_label_form_id,			facility_in.facility_location_label_form_id,			facility_in.sterilizer_label_form_id,			facility_in.user_badge_label_form_id,			facility_in.workstation_label_form_id,			facility_in.default_spd_facility_id,			facility_in.created_dts,			facility_in.updated_dts,			facility_in.sonar_auto_logout_minutes,			facility_in.sonar_auto_logout_dialog_minutes,			facility_in.num_ors,			facility_in.sonar_go_live_date,			facility_in.marked_for_deletion,			facility_in.is_count_sheet_source_truth,			facility_in.hide_doc_names_for_stats,			facility_in.is_item_master_live,			facility_in.is_sonar_live,			facility_in.sterilizer_print_at_sterilizing,			facility_in.sterilizer_print_at_cooling,			facility_in.sterilizer_print_at_released,			facility_in.can_set_all_found_in_assembly,			facility_in.name_,			facility_in.their_id,			facility_in.created_by,			facility_in.updated_by,			facility_in.reporting_name,			facility_in.label_name,			facility_in.tz_name,			facility_in.sonar_name_full,			facility_in.sonar_count_sheet_form_name,			facility_in.sterilize_alert_level,			facility_in.sonar_scanner_stop_char,			facility_in.emr_location_name,			facility_in.sonar_features,			facility_in.interfaces,			facility_in.needs_scheduling,			facility_in.analytics_prefs               $BODY$LANGUAGE sql;  ALTER FUNCTION types_plus.facility_to_v2 (facility_in facility)	OWNER TO user_bender;-- Create/recreate the CAST. Use this to convert facility rows into the facility_v2 compound type format using the following syntax:-- select facility::facility_v2 from facilityDROP CAST IF EXISTS (facility as facility_v2);CREATE CAST (facility as facility_v2) WITH FUNCTION types_plus.facility_to_v2(facility);-- Create a function to accept an array of rows formatted as facility_v2 for UPSERT into facility.DROP FUNCTION IF EXISTS types_plus.insert_facility_v2 (types_plus.facility_v2[]);  CREATE OR REPLACE FUNCTION types_plus.insert_facility_v2 (data_in types_plus.facility_v2[])  RETURNS intAS $BODY$ -- The CTE below is a roundabout way of returning an insertion count from a pure SQL function in Postgres.with inserted_rows as (        INSERT INTO facility (			id,			hsys_id,			production_label_form_id,			permanent_label_form_id,			missing_inst_label_form_id,			facility_location_label_form_id,			sterilizer_label_form_id,			user_badge_label_form_id,			workstation_label_form_id,			default_spd_facility_id,			created_dts,			updated_dts,			sonar_auto_logout_minutes,			sonar_auto_logout_dialog_minutes,			num_ors,			sonar_go_live_date,			marked_for_deletion,			is_count_sheet_source_truth,			hide_doc_names_for_stats,			is_item_master_live,			is_sonar_live,			sterilizer_print_at_sterilizing,			sterilizer_print_at_cooling,			sterilizer_print_at_released,			can_set_all_found_in_assembly,			name_,			their_id,			created_by,			updated_by,			reporting_name,			label_name,			tz_name,			sonar_name_full,			sonar_count_sheet_form_name,			sterilize_alert_level,			sonar_scanner_stop_char,			emr_location_name,			sonar_features,			interfaces,			needs_scheduling,			analytics_prefs)                  SELECT			rows_in.id,			rows_in.hsys_id,			rows_in.production_label_form_id,			rows_in.permanent_label_form_id,			rows_in.missing_inst_label_form_id,			rows_in.facility_location_label_form_id,			rows_in.sterilizer_label_form_id,			rows_in.user_badge_label_form_id,			rows_in.workstation_label_form_id,			rows_in.default_spd_facility_id,			rows_in.created_dts,			rows_in.updated_dts,			rows_in.sonar_auto_logout_minutes,			rows_in.sonar_auto_logout_dialog_minutes,			rows_in.num_ors,			rows_in.sonar_go_live_date,			rows_in.marked_for_deletion,			rows_in.is_count_sheet_source_truth,			rows_in.hide_doc_names_for_stats,			rows_in.is_item_master_live,			rows_in.is_sonar_live,			rows_in.sterilizer_print_at_sterilizing,			rows_in.sterilizer_print_at_cooling,			rows_in.sterilizer_print_at_released,			rows_in.can_set_all_found_in_assembly,			rows_in.name_,			rows_in.their_id,			rows_in.created_by,			rows_in.updated_by,			rows_in.reporting_name,			rows_in.label_name,			rows_in.tz_name,			rows_in.sonar_name_full,			rows_in.sonar_count_sheet_form_name,			rows_in.sterilize_alert_level,			rows_in.sonar_scanner_stop_char,			rows_in.emr_location_name,			rows_in.sonar_features,			rows_in.interfaces,			rows_in.needs_scheduling,			rows_in.analytics_prefs                      FROM unnest(data_in) as rows_in                  ON CONFLICT(id) DO UPDATE SET			hsys_id = EXCLUDED.hsys_id,			production_label_form_id = EXCLUDED.production_label_form_id,			permanent_label_form_id = EXCLUDED.permanent_label_form_id,			missing_inst_label_form_id = EXCLUDED.missing_inst_label_form_id,			facility_location_label_form_id = EXCLUDED.facility_location_label_form_id,			sterilizer_label_form_id = EXCLUDED.sterilizer_label_form_id,			user_badge_label_form_id = EXCLUDED.user_badge_label_form_id,			workstation_label_form_id = EXCLUDED.workstation_label_form_id,			default_spd_facility_id = EXCLUDED.default_spd_facility_id,			created_dts = EXCLUDED.created_dts,			updated_dts = EXCLUDED.updated_dts,			sonar_auto_logout_minutes = EXCLUDED.sonar_auto_logout_minutes,			sonar_auto_logout_dialog_minutes = EXCLUDED.sonar_auto_logout_dialog_minutes,			num_ors = EXCLUDED.num_ors,			sonar_go_live_date = EXCLUDED.sonar_go_live_date,			marked_for_deletion = EXCLUDED.marked_for_deletion,			is_count_sheet_source_truth = EXCLUDED.is_count_sheet_source_truth,			hide_doc_names_for_stats = EXCLUDED.hide_doc_names_for_stats,			is_item_master_live = EXCLUDED.is_item_master_live,			is_sonar_live = EXCLUDED.is_sonar_live,			sterilizer_print_at_sterilizing = EXCLUDED.sterilizer_print_at_sterilizing,			sterilizer_print_at_cooling = EXCLUDED.sterilizer_print_at_cooling,			sterilizer_print_at_released = EXCLUDED.sterilizer_print_at_released,			can_set_all_found_in_assembly = EXCLUDED.can_set_all_found_in_assembly,			name_ = EXCLUDED.name_,			their_id = EXCLUDED.their_id,			created_by = EXCLUDED.created_by,			updated_by = EXCLUDED.updated_by,			reporting_name = EXCLUDED.reporting_name,			label_name = EXCLUDED.label_name,			tz_name = EXCLUDED.tz_name,			sonar_name_full = EXCLUDED.sonar_name_full,			sonar_count_sheet_form_name = EXCLUDED.sonar_count_sheet_form_name,			sterilize_alert_level = EXCLUDED.sterilize_alert_level,			sonar_scanner_stop_char = EXCLUDED.sonar_scanner_stop_char,			emr_location_name = EXCLUDED.emr_location_name,			sonar_features = EXCLUDED.sonar_features,			interfaces = EXCLUDED.interfaces,			needs_scheduling = EXCLUDED.needs_scheduling,			analytics_prefs = EXCLUDED.analytics_prefs          returning 1 as row_counter)         select sum(row_counter)::integer from inserted_rows; $BODY$LANGUAGE sql;  ALTER FUNCTION types_plus.insert_facility_v2(types_plus.facility_v2[])	OWNER TO user_bender;