-- Create a view onto facility formatted as facility_v2.-- If facility changes, you can update the view to handle the modification smoothly.-- Note: This view is automatically a custom type, with an array type to match. See types_plus.insert_facility_v2.DROP VIEW IF EXISTS types_plus.facility_v2 CASCADE;-- Careful!CREATE OR REPLACE VIEW types_plus.facility_v2 AS select id,        hsys_id,        production_label_form_id,        permanent_label_form_id,        missing_inst_label_form_id,        facility_location_label_form_id,        sterilizer_label_form_id,        user_badge_label_form_id,        workstation_label_form_id,        default_spd_facility_id,        created_dts,        updated_dts,        sonar_auto_logout_minutes,        sonar_auto_logout_dialog_minutes,        num_ors,        sonar_go_live_date,        marked_for_deletion,        is_count_sheet_source_truth,        hide_doc_names_for_stats,        is_item_master_live,        is_sonar_live,        sterilizer_print_at_sterilizing,        sterilizer_print_at_cooling,        sterilizer_print_at_released,        can_set_all_found_in_assembly,        name_,        their_id,        created_by,        updated_by,        reporting_name,        label_name,        tz_name,        sonar_name_full,        sonar_count_sheet_form_name,        sterilize_alert_level,        sonar_scanner_stop_char,        emr_location_name,        sonar_features,        interfaces,        needs_scheduling,        analytics_prefs   from facility;ALTER TABLE types_plus.facility_v2    OWNER TO user_change_structure;