-- Create a casting function to convert focus rows into the compound type format focus_v2.-- If focus changes, you can update the casting to handle the modification smoothly.CREATE OR REPLACE FUNCTION types_plus.focus_to_v2 (focus_in focus)  RETURNS focus_v2AS $BODY$                   SELECT			focus_in.scase_id,			focus_in.hsys_id,			focus_in.facility_id,			focus_in.sched_dts,			focus_in.facility_name,			focus_in.or_name,			focus_in.case_seq,			focus_in.desc_,			focus_in.status,			focus_in.transport_from,			focus_in.surgeon_name,			focus_in.proc_name,			focus_in.when_               $BODY$LANGUAGE sql;  ALTER FUNCTION types_plus.focus_to_v2 (focus_in focus)	OWNER TO user_bender;-- Create/recreate the CAST. Use this to convert focus rows into the focus_v2 compound type format using the following syntax:-- select focus::focus_v2 from focusDROP CAST IF EXISTS (focus as focus_v2);CREATE CAST (focus as focus_v2) WITH FUNCTION types_plus.focus_to_v2(focus);