-- Create a view onto focus formatted as focus_v2.-- If focus changes, you can update the view to handle the modification smoothly.-- Note: This view is automatically a custom type, with an array type to match. See types_plus.insert_focus_v2.DROP VIEW IF EXISTS types_plus.focus_v2 CASCADE;-- Careful!CREATE OR REPLACE VIEW types_plus.focus_v2 AS select scase_id,        hsys_id,        facility_id,        sched_dts,        facility_name,        or_name,        case_seq,        desc_,        status,        transport_from,        surgeon_name,        proc_name,        when_   from focus;ALTER TABLE types_plus.focus_v2    OWNER TO user_change_structure;