-- Create a function to accept an array of rows formatted as form_template_v1 for UPSERT into form_template.DROP FUNCTION IF EXISTS types_plus.insert_form_template_v1 (types_plus.form_template_v1[]);  CREATE OR REPLACE FUNCTION types_plus.insert_form_template_v1 (data_in types_plus.form_template_v1[])  RETURNS intAS $BODY$ -- The CTE below is a roundabout way of returning an insertion count from a pure SQL function in Postgres.with inserted_rows as (        INSERT INTO form_template (			id,			created_dts,			updated_dts,			marked_for_deletion,			name_,			comments_,			updated_by,			created_by,			form_definition)                  SELECT			rows_in.id,			rows_in.created_dts,			rows_in.updated_dts,			rows_in.marked_for_deletion,			rows_in.name_,			rows_in.comments_,			rows_in.updated_by,			rows_in.created_by,			rows_in.form_definition                      FROM unnest(data_in) as rows_in                  ON CONFLICT(id) DO UPDATE SET			created_dts = EXCLUDED.created_dts,			updated_dts = EXCLUDED.updated_dts,			marked_for_deletion = EXCLUDED.marked_for_deletion,			name_ = EXCLUDED.name_,			comments_ = EXCLUDED.comments_,			updated_by = EXCLUDED.updated_by,			created_by = EXCLUDED.created_by,			form_definition = EXCLUDED.form_definition          returning 1 as row_counter)         select sum(row_counter)::integer from inserted_rows; $BODY$LANGUAGE sql;  ALTER FUNCTION types_plus.insert_form_template_v1(types_plus.form_template_v1[])	OWNER TO user_bender;