-- Create a view onto hsys formatted as hsys_v1.-- If hsys changes, you can update the view to handle the modification smoothly.-- Note: This view is automatically a custom type, with an array type to match. See types_plus.insert_hsys_v1.DROP VIEW IF EXISTS types_plus.hsys_v1 CASCADE;-- Careful!CREATE OR REPLACE VIEW types_plus.hsys_v1 AS select id,        marked_for_deletion,        name_   from hsys;ALTER TABLE types_plus.hsys_v1    OWNER TO user_change_structure;-- Create a casting function to convert hsys rows into the compound type format hsys_v1.-- If hsys changes, you can update the casting to handle the modification smoothly.CREATE OR REPLACE FUNCTION types_plus.hsys_to_v1 (hsys_in hsys)  RETURNS hsys_v1AS $BODY$        SELECT			hsys_in.id,			hsys_in.marked_for_deletion,			hsys_in.name_$BODY$LANGUAGE sql;ALTER FUNCTION types_plus.hsys_to_v1 (hsys_in hsys) OWNER TO user_bender;-- Create/recreate the CAST. Use this to convert hsys rows into the hsys_v1 compound type format using the following syntax:-- select hsys::hsys_v1 from hsysDROP CAST IF EXISTS (hsys as hsys_v1);CREATE CAST (hsys as hsys_v1) WITH FUNCTION types_plus.hsys_to_v1(hsys);-- Create a function to accept an array of rows formatted as hsys_v1 for UPSERT into hsys.DROP FUNCTION IF EXISTS types_plus.insert_hsys_v1 (types_plus.hsys_v1[]);CREATE OR REPLACE FUNCTION types_plus.insert_hsys_v1 (data_in types_plus.hsys_v1[])  RETURNS intAS $BODY$-- The CTE below is a roundabout way of returning an insertion count from a pure SQL function in Postgres.with inserted_rows as (        INSERT INTO hsys (			id,			marked_for_deletion,			name_)        SELECT			rows_in.id,			rows_in.marked_for_deletion,			rows_in.name_        FROM unnest(data_in) as rows_in        ON CONFLICT(id) DO UPDATE SET			marked_for_deletion = EXCLUDED.marked_for_deletion,			name_ = EXCLUDED.name_        returning 1 as row_counter)    select sum(row_counter)::integer from inserted_rows;$BODY$LANGUAGE sql;ALTER FUNCTION types_plus.insert_hsys_v1(types_plus.hsys_v1[]) OWNER TO user_bender;