-- Create a function to accept an array of rows formatted as hsys_v1 for UPSERT into hsys.DROP FUNCTION IF EXISTS types_plus.insert_hsys_v1 (types_plus.hsys_v1[]);CREATE OR REPLACE FUNCTION types_plus.insert_hsys_v1 (data_in types_plus.hsys_v1[])  RETURNS intAS $BODY$-- The CTE below is a roundabout way of returning an insertion count from a pure SQL function in Postgres.with inserted_rows as (        INSERT INTO hsys (			id,			marked_for_deletion,			name_)        SELECT			rows_in.id,			rows_in.marked_for_deletion,			rows_in.name_        FROM unnest(data_in) as rows_in        ON CONFLICT(id) DO UPDATE SET			marked_for_deletion = EXCLUDED.marked_for_deletion,			name_ = EXCLUDED.name_        returning 1 as row_counter)    select sum(row_counter)::integer from inserted_rows;$BODY$LANGUAGE sql;ALTER FUNCTION types_plus.insert_hsys_v1(types_plus.hsys_v1[]) OWNER TO user_bender;