-- Create a function to accept an array of rows formatted as item_arch_v1 for UPSERT into item_arch.DROP FUNCTION IF EXISTS types_plus.insert_item_arch_v1 (types_plus.item_arch_v1[]);  CREATE OR REPLACE FUNCTION types_plus.insert_item_arch_v1 (data_in types_plus.item_arch_v1[])  RETURNS intAS $BODY$ -- The CTE below is a roundabout way of returning an insertion count from a pure SQL function in Postgres.with inserted_rows as (        INSERT INTO item_arch (			id,			facility_id,			clinic_id,			created_dts,			updated_dts,			previous_as_of,			marked_for_deletion,			is_current,			name_,			type_,			created_by,			updated_by)                  SELECT			rows_in.id,			rows_in.facility_id,			rows_in.clinic_id,			rows_in.created_dts,			rows_in.updated_dts,			rows_in.previous_as_of,			rows_in.marked_for_deletion,			rows_in.is_current,			rows_in.name_,			rows_in.type_,			rows_in.created_by,			rows_in.updated_by                      FROM unnest(data_in) as rows_in                  ON CONFLICT(id) DO UPDATE SET			facility_id = EXCLUDED.facility_id,			clinic_id = EXCLUDED.clinic_id,			created_dts = EXCLUDED.created_dts,			updated_dts = EXCLUDED.updated_dts,			previous_as_of = EXCLUDED.previous_as_of,			marked_for_deletion = EXCLUDED.marked_for_deletion,			is_current = EXCLUDED.is_current,			name_ = EXCLUDED.name_,			type_ = EXCLUDED.type_,			created_by = EXCLUDED.created_by,			updated_by = EXCLUDED.updated_by          returning 1 as row_counter)         select sum(row_counter)::integer from inserted_rows; $BODY$LANGUAGE sql;  ALTER FUNCTION types_plus.insert_item_arch_v1(types_plus.item_arch_v1[])	OWNER TO user_bender;