-- Create a casting function to convert item_type rows into the compound type format item_type_v1.-- If item_type changes, you can update the casting to handle the modification smoothly.CREATE OR REPLACE FUNCTION types_plus.item_type_to_v1 (item_type_in item_type)  RETURNS item_type_v1AS $BODY$        SELECT			item_type_in.id,			item_type_in.marked_for_deletion,			item_type_in.name_$BODY$LANGUAGE sql;ALTER FUNCTION types_plus.item_type_to_v1 (item_type_in item_type) OWNER TO user_bender;-- Create/recreate the CAST. Use this to convert item_type rows into the item_type_v1 compound type format using the following syntax:-- select item_type::item_type_v1 from item_typeDROP CAST IF EXISTS (item_type as item_type_v1);CREATE CAST (item_type as item_type_v1) WITH FUNCTION types_plus.item_type_to_v1(item_type);