-- Create a view onto item_type formatted as item_type_v1.-- If item_type changes, you can update the view to handle the modification smoothly.-- Note: This view is automatically a custom type, with an array type to match. See types_plus.insert_item_type_v1.DROP VIEW IF EXISTS types_plus.item_type_v1 CASCADE;-- Careful!CREATE OR REPLACE VIEW types_plus.item_type_v1 AS select id,        marked_for_deletion,        name_   from item_type;ALTER TABLE types_plus.item_type_v1    OWNER TO user_change_structure;-- Create a casting function to convert item_type rows into the compound type format item_type_v1.-- If item_type changes, you can update the casting to handle the modification smoothly.CREATE OR REPLACE FUNCTION types_plus.item_type_to_v1 (item_type_in item_type)  RETURNS item_type_v1AS $BODY$        SELECT			item_type_in.id,			item_type_in.marked_for_deletion,			item_type_in.name_$BODY$LANGUAGE sql;ALTER FUNCTION types_plus.item_type_to_v1 (item_type_in item_type) OWNER TO user_bender;-- Create/recreate the CAST. Use this to convert item_type rows into the item_type_v1 compound type format using the following syntax:-- select item_type::item_type_v1 from item_typeDROP CAST IF EXISTS (item_type as item_type_v1);CREATE CAST (item_type as item_type_v1) WITH FUNCTION types_plus.item_type_to_v1(item_type);-- Create a function to accept an array of rows formatted as item_type_v1 for UPSERT into item_type.DROP FUNCTION IF EXISTS types_plus.insert_item_type_v1 (types_plus.item_type_v1[]);CREATE OR REPLACE FUNCTION types_plus.insert_item_type_v1 (data_in types_plus.item_type_v1[])  RETURNS intAS $BODY$-- The CTE below is a roundabout way of returning an insertion count from a pure SQL function in Postgres.with inserted_rows as (        INSERT INTO item_type (			id,			marked_for_deletion,			name_)        SELECT			rows_in.id,			rows_in.marked_for_deletion,			rows_in.name_        FROM unnest(data_in) as rows_in        ON CONFLICT(id) DO UPDATE SET			marked_for_deletion = EXCLUDED.marked_for_deletion,			name_ = EXCLUDED.name_        returning 1 as row_counter)    select sum(row_counter)::integer from inserted_rows;$BODY$LANGUAGE sql;ALTER FUNCTION types_plus.insert_item_type_v1(types_plus.item_type_v1[]) OWNER TO user_bender;