-- Create a casting function to convert item rows into the compound type format item_v2.-- If item changes, you can update the casting to handle the modification smoothly.CREATE OR REPLACE FUNCTION types_plus.item_to_v2 (item_in item)  RETURNS item_v2AS $BODY$                   SELECT			item_in.id,			item_in.item_arch_id,			item_in.facility_specialty_id,			item_in.item_uu_id_across_item_archs,			item_in.inv_responsible_user_id,			item_in.facility_department_id,			item_in.specialty_id,			item_in.sup_id,			item_in.dr_people_id,			item_in.item_type_id,			item_in.item_master_link_id,			item_in.facility_location_id,			item_in.clinic_id,			item_in.hsys_tag_id,			item_in.name_copied_from_prod_id,			item_in.created_dts,			item_in.updated_dts,			item_in.named_dts,			item_in.needs_sched_sync_as_of_dts,			item_in.weight_lb,			item_in.qty,			item_in.flow,			item_in.flow_long,			item_in.max_processed_one_day,			item_in.par,			item_in.assembly_difficulty,			item_in.num_inst,			item_in.repair_every_x,			item_in.their_qty,			item_in.replace_every_x_cycles,			item_in.hide_specialty_in_name,			item_in.marked_for_deletion,			item_in.is_assembly_critical,			item_in.requires_leak_test,			item_in.is_not_tracked_by_inv_no,			item_in.is_imlink_locked,			item_in.is_robotic,			item_in.is_flexible_scope,			item_in.is_generic_pack,			item_in.dont_print_count_sheet,			item_in.is_conflict_check,			item_in.is_small_set,			item_in.hide_hsys_tag_in_name,			item_in.is_name_finalized,			item_in.is_in_data_cleanse_scope,			item_in.is_label_abbr,			item_in.has_implants,			item_in.sync_to_other_software_systems,			item_in.inv_has_implants,			item_in.is_custom_sort,			item_in.is_name_copied_from_prod,			item_in.is_deactivated,			item_in.is_vendor_data,			item_in.name_needs_review,			item_in.ignore_for_optimization,			item_in.inv_status,			item_in.their_name,			item_in.opt_status,			item_in.created_by,			item_in.updated_by,			item_in.old_names,			item_in.their_serial_no,			item_in.name_desc,			item_in.name_,			item_in.their_id,			item_in.decon_special_instructions,			item_in.named_by,			item_in.needs_review_notes,			item_in.vendor_tray_type,			item_in.sterilization_method,			item_in.priority,			item_in.category,			item_in.packaging_type,			item_in.packaging_notes,			item_in.their_packaging_type,			item_in.assembly_special_instructions,			item_in.sterilize_special_instructions,			item_in.label_name_abbr,			item_in.their_location,			item_in.count_sheet_status,			item_in.requested_by,			item_in.rack_row_bin_note,			item_in.standardization_status,			item_in.repair_every_x_type,			item_in.their_base_barcode,			item_in.their_key,			item_in.stuff               $BODY$LANGUAGE sql;  ALTER FUNCTION types_plus.item_to_v2 (item_in item)	OWNER TO user_bender;-- Create/recreate the CAST. Use this to convert item rows into the item_v2 compound type format using the following syntax:-- select item::item_v2 from itemDROP CAST IF EXISTS (item as item_v2);CREATE CAST (item as item_v2) WITH FUNCTION types_plus.item_to_v2(item);