-- Create a casting function to convert loaner_iteminv rows into the compound type format loaner_iteminv_v1.-- If loaner_iteminv changes, you can update the casting to handle the modification smoothly.CREATE OR REPLACE FUNCTION types_plus.loaner_iteminv_to_v1 (loaner_iteminv_in loaner_iteminv)  RETURNS loaner_iteminv_v1AS $BODY$                   SELECT			loaner_iteminv_in.id,			loaner_iteminv_in.loaner_item_id,			loaner_iteminv_in.inv_id,			loaner_iteminv_in.last_updated_by_data_file_id,			loaner_iteminv_in.created_dts,			loaner_iteminv_in.updated_dts,			loaner_iteminv_in.marked_for_deletion,			loaner_iteminv_in.created_by,			loaner_iteminv_in.updated_by               $BODY$LANGUAGE sql;  ALTER FUNCTION types_plus.loaner_iteminv_to_v1 (loaner_iteminv_in loaner_iteminv)	OWNER TO user_bender;-- Create/recreate the CAST. Use this to convert loaner_iteminv rows into the loaner_iteminv_v1 compound type format using the following syntax:-- select loaner_iteminv::loaner_iteminv_v1 from loaner_iteminvDROP CAST IF EXISTS (loaner_iteminv as loaner_iteminv_v1);CREATE CAST (loaner_iteminv as loaner_iteminv_v1) WITH FUNCTION types_plus.loaner_iteminv_to_v1(loaner_iteminv);