-- Create a casting function to convert loaner rows into the compound type format loaner_v1.-- If loaner changes, you can update the casting to handle the modification smoothly.CREATE OR REPLACE FUNCTION types_plus.loaner_to_v1 (loaner_in loaner)  RETURNS loaner_v1AS $BODY$                   SELECT			loaner_in.s_case_id,			loaner_in.id,			loaner_in.sup_id,			loaner_in.rep_id,			loaner_in.facility_id,			loaner_in.last_updated_by_data_file_id,			loaner_in.pickup_dts,			loaner_in.created_dts,			loaner_in.updated_dts,			loaner_in.datetime_local,			loaner_in.drop_off_dts,			loaner_in.num_items,			loaner_in.marked_for_deletion,			loaner_in.is_printed,			loaner_in.created_by,			loaner_in.updated_by,			loaner_in.status,			loaner_in.notes               $BODY$LANGUAGE sql;  ALTER FUNCTION types_plus.loaner_to_v1 (loaner_in loaner)	OWNER TO user_bender;-- Create/recreate the CAST. Use this to convert loaner rows into the loaner_v1 compound type format using the following syntax:-- select loaner::loaner_v1 from loanerDROP CAST IF EXISTS (loaner as loaner_v1);CREATE CAST (loaner as loaner_v1) WITH FUNCTION types_plus.loaner_to_v1(loaner);