-- Create a casting function to convert need rows into the compound type format need_v5.-- If need changes, you can update the casting to handle the modification smoothly.CREATE OR REPLACE FUNCTION types_plus.need_to_v5 (need_in need)  RETURNS need_v5AS $BODY$                   SELECT			need_in.id,			need_in.hsys_id,			need_in.facility_id,			need_in.item_id,			need_in.percent_down,			need_in.next_case_dts,			need_in.flow,			need_in.qty_circulation,			need_in.qty_ready_everywhere,			need_in.qty_ready_here,			need_in.qty_sched_everywhere,			need_in.qty_sched_here,			need_in.qty_sterile_everywhere,			need_in.qty_sterile_here,			need_in.qty_suggest_sterilize,			need_in.qty_total,			need_in.ready_vs_need_defecit,			need_in.sched_sort,			need_in.sort_rank,			need_in.suggest_transport,			need_in.next_needed,			need_in.or_name,			need_in.status,			need_in.status_sort,			need_in.when_,			need_in.qty_here               $BODY$LANGUAGE sql;  ALTER FUNCTION types_plus.need_to_v5 (need_in need)	OWNER TO user_bender;-- Create/recreate the CAST. Use this to convert need rows into the need_v5 compound type format using the following syntax:-- select need::need_v5 from needDROP CAST IF EXISTS (need as need_v5);CREATE CAST (need as need_v5) WITH FUNCTION types_plus.need_to_v5(need);