-- Create a view onto need formatted as need_v5.-- If need changes, you can update the view to handle the modification smoothly.-- Note: This view is automatically a custom type, with an array type to match. See types_plus.insert_need_v5.DROP VIEW IF EXISTS types_plus.need_v5 CASCADE;-- Careful!CREATE OR REPLACE VIEW types_plus.need_v5 AS select id,        hsys_id,        facility_id,        item_id,        percent_down,        next_case_dts,        flow,        qty_circulation,        qty_ready_everywhere,        qty_ready_here,        qty_sched_everywhere,        qty_sched_here,        qty_sterile_everywhere,        qty_sterile_here,        qty_suggest_sterilize,        qty_total,        ready_vs_need_defecit,        sched_sort,        sort_rank,        suggest_transport,        next_needed,        or_name,        status,        status_sort,        when_,        qty_here   from need;ALTER TABLE types_plus.need_v5    OWNER TO user_change_structure;