-- Create a view onto productivity_detail formatted as productivity_detail_v1.-- If productivity_detail changes, you can update the view to handle the modification smoothly.-- Note: This view is automatically a custom type, with an array type to match. See types_plus.insert_productivity_detail_v1.DROP VIEW IF EXISTS types_plus.productivity_detail_v1 CASCADE;-- Careful!CREATE OR REPLACE VIEW types_plus.productivity_detail_v1 AS select id,        source_record_row_counter,        start_time_dts,        source_table_number,        data_file_id,        user_id,        facility_id,        assembly_inst,        assembly_pack,        assembly_tray,        assembly_points,        assembly_seconds_trays,        assembly_seconds_overall,        pause_seconds,        duration_seconds,        placeholder_row,        description,        year_and_month,        start_date,        time_label,        duration,        pause_time,        activity,        user_label   from productivity_detail;ALTER TABLE types_plus.productivity_detail_v1    OWNER TO user_change_structure;