-- Create a view onto push_audit formatted as push_audit_v1.-- If push_audit changes, you can update the view to handle the modification smoothly.-- Note: This view is automatically a custom type, with an array type to match. See types_plus.insert_push_audit_v1.DROP VIEW IF EXISTS types_plus.push_audit_v1 CASCADE;-- Careful!CREATE OR REPLACE VIEW types_plus.push_audit_v1 AS select target.schema_name,        target.target_name,        target.unique_path,        target.target_type,        audit.data_file_id,        audit.duration_seconds,        audit.records_count,        audit.version_number,        audit.start_local_dts,        audit.end_local_dts,        audit.end_utc_dts,        audit.error_occurred,        audit.client_user_name,        audit.push_method_name,        audit.error_text     from push_audit     auditleft join dba.push_target    target on target.id = audit.target_id;