-- Create a casting function to convert push_audit rows into the compound type format push_audit_v2.-- If push_audit changes, you can update the casting to handle the modification smoothly.CREATE OR REPLACE FUNCTION types_plus.push_audit_to_v2 (push_audit_in push_audit)  RETURNS push_audit_v2AS $BODY$                   SELECT			push_audit_in.id,			push_audit_in.target_id,			push_audit_in.data_file_id,			push_audit_in.pg_config_target_version_id,			push_audit_in.start_local_dts,			push_audit_in.end_utc_dts,			push_audit_in.end_local_dts,			push_audit_in.inserted_utc_dts,			push_audit_in.first_con_id,			push_audit_in.last_con_id,			push_audit_in.records_count,			push_audit_in.duration_seconds,			push_audit_in.error_occurred,			push_audit_in.client_user_name,			push_audit_in.inserted_by,			push_audit_in.push_method_name,			push_audit_in.error_text,			push_audit_in.result_text               $BODY$LANGUAGE sql;  ALTER FUNCTION types_plus.push_audit_to_v2 (push_audit_in push_audit)	OWNER TO user_bender;-- Create/recreate the CAST. Use this to convert push_audit rows into the push_audit_v2 compound type format using the following syntax:-- select push_audit::push_audit_v2 from push_auditDROP CAST IF EXISTS (push_audit as push_audit_v2);CREATE CAST (push_audit as push_audit_v2) WITH FUNCTION types_plus.push_audit_to_v2(push_audit);