-- Create a casting function to convert q_audit_step rows into the compound type format q_audit_step_v1.-- If q_audit_step changes, you can update the casting to handle the modification smoothly.CREATE OR REPLACE FUNCTION types_plus.q_audit_step_to_v1 (q_audit_step_in q_audit_step)  RETURNS q_audit_step_v1AS $BODY$                   SELECT			q_audit_step_in.id,			q_audit_step_in.hsys_id,			q_audit_step_in.qsub_type_id_audit,			q_audit_step_in.step,			q_audit_step_in.marked_for_deletion,			q_audit_step_in.last_updated_by_data_file_id,			q_audit_step_in.created_dts,			q_audit_step_in.updated_dts,			q_audit_step_in.created_by,			q_audit_step_in.updated_by,			q_audit_step_in.sort,			q_audit_step_in.qsub_type_id_event               $BODY$LANGUAGE sql;  ALTER FUNCTION types_plus.q_audit_step_to_v1 (q_audit_step_in q_audit_step)	OWNER TO user_bender;-- Create/recreate the CAST. Use this to convert q_audit_step rows into the q_audit_step_v1 compound type format using the following syntax:-- select q_audit_step::q_audit_step_v1 from q_audit_stepDROP CAST IF EXISTS (q_audit_step as q_audit_step_v1);CREATE CAST (q_audit_step as q_audit_step_v1) WITH FUNCTION types_plus.q_audit_step_to_v1(q_audit_step);